def hex_to_colums(hex_number):
    ser_number = hex_number
    ser_hex = str(hex(ser_number))[2:]
    if len(ser_hex[2:]) % 2 != 0:
        ser_hex = "0" + ser_hex
    hex_with_colons = ':'.join(ser_hex[i:i + 2] for i in range(0, len(ser_hex), 2))
    return hex_with_colons


